INSERT INTO `neuws` (`id`, `title_en`, `title_fr`, `url_en`, `url_fr`, `summary_en`, `summary_fr`, `created_at`, `updated_at`) VALUES
	(2, 'Soil Landscapes of Canada, version 3.2', 'Pédo-paysages du Canada, version 3.2', '/cansis/nsdb/slc/v3.2/index.html', '/siscan/nsdb/slc/v3.2/index.html', 'SLC version 3.2 is the latest revision of the Soil Landscapes of Canada, which was developed by Agriculture and Agri-Food Canada. ', 'PPC v3.2 (2010) est la plus récente version des pédo-paysages du Canada produite par Agriculture et Agroalimentaire Canada. ', '2011-03-07 19:00:00', '2012-02-07 17:18:39'),
	(3, 'Ecodistrict names', 'Noms des écodistricts', '/cansis/nsdb/ecostrat/district/dt_names.dbf', '/cansis/nsdb/ecostrat/district/dt_names.dbf', 'Ecodistrict names dbase file.', 'Noms des écodistricts fichier dbase.', '2011-02-14 19:00:00', '2012-02-07 15:33:52'),
	(4, 'CLI 250K Agriculture scanned maps', 'Inventaire des terres du Canada (ITC) 1:250.000 Possibilitées agricoles des sols', '/cansis/publications/maps/cli/250k/agr/index.html', '/siscan/publications/maps/cli/250k/agr/index.html', 'CLI maps published by Canada\'s ARDA programme between 1961 and 1978 at a scale of 1:250,000.', 'Cartes publiées entre 1961 et 1978 à une échelle de 1:250,000.', '2010-12-02 19:00:00', '2011-11-02 10:23:42'),
	(6, 'Photo Gallery', 'Gallerie de photos', '/cansis/images/index.html', '/siscan/images/index.html', 'Canada Soil Landscape Illustrations', 'Illustrations de pédo-paysages du Canada', '2010-06-30 20:00:00', '2012-02-06 19:20:38'),
	(7, 'Access to soil data via Table Joining Service (TJS)', 'L\'accès aux données sur les sols via le service TJS', '', '', 'TJS is a web service that supports automated exchange of information and mapping. It does not produce human-readable HTML pages.', 'TJS est un service web qui permet l\'échange automatisé d\'informations et de cartographie. Par contre, il ne produit pas de pages HTML lisibles.', '2010-05-31 20:00:00', '2012-02-09 17:11:24'),
	(9, 'CSSC3 Web Pages', 'SCCS3 version électronique', '/cansis/taxa/cssc3/index.html', '/siscan/taxa/cssc3/index.html', 'This revised publication replaces The Canadian System of Soil Classification (second edition) published in 1987.', 'Cette publication révisée remplace le Système canadien de classification des sols (deuxième édition), publiée en 1987.\r\n', '2010-02-28 19:00:00', '2012-02-08 19:14:55'),
	(10, 'CSSC3 Taxonomy Service', 'Service de Taxonomie SCCS3', '', '', 'This service creates different views of the Canadian System for Soil Classification taxonomy.', 'Ce service crée des vues différentes de la taxonomie du Système canadien de classification des sols.', '2010-02-28 19:00:00', '2012-02-10 14:24:31'),
	(11, 'National Ecological Framework map (improved)', 'Carte du Cadre écologique national pour le Canada (amélioré)', '', '', 'The National Ecological Framework provides a consistent, national spatial context within which ecosystems at various levels of generalization can be described, monitored, and reported on.', 'Le cadre écologique national fourni un contexte spatial national cohérent pour décrire et surveiller l\'évolution des écosystèmes à divers niveaux de généralisation.', '2010-02-28 19:00:00', '2012-02-07 17:20:36'),
	(12, 'Soils of Canada map', 'Carte des sols du Canada', '', '', 'These national scale thematic maps display the distribution and areal extent of soil attributes such as drainage, texture of parent material, kind of material, and classification of soils in terms of Soil Order and Great Group. ', 'Ces cartes thématiques présentent la distribution et l\'étendue des attributs des sols tels que le drainage, la texture du matériau originel, le type de matériau et la classification des sols en tenant compte des ordres et des grands groupes de sols.', '2010-02-28 19:00:00', '2012-02-07 17:21:32'),
	(13, 'AgroPedological Atlas of the Montreal Plain (improved)', 'Atlas agropédologique du sud-est de la plaine de Montréal, Québec (amélioré)', '', '', 'The Agro-Pedological Atlas is a set of interactive maps which show the characteristics, the fertility, the quality of the water regime, the vulnerability to degradation and the potential of the agricultural soils and land in the Monteregian region of the province of Quebec.', 'L’Atlas agropédologique est un ensemble de cartes interactives qui montrent les caractéristiques, la fertilité, la qualité du régime hydrique, la vulnérabilité à la dégradation et le potentiel des sols et des terres agricoles situées en Montérégie dans la province de Québec. ', '2010-01-31 19:00:00', '2012-02-08 15:24:22'),
	(14, 'Glossary of Terms in Soil Science (improved)', 'Glossaire des termes de la science des sols (amélioré)', '/cansis/glossary/a/index.html', '/siscan/glossary/a/index.html', 'Glossary of Terms in Soil Science', 'Glossaire des termes de la science des sols', '2010-01-31 19:00:00', '2012-03-30 17:01:33'),
	(15, 'Soil Layers Data Service', 'Service de données de la Table de couches de sol', '', '', 'This service provides access to the contents of the Soil Layers Table.', 'Ce service donne accès au contenu de la Table de couches de sol.', '2009-11-30 19:00:00', '2012-02-08 18:46:06'),
	(16, 'Soil Names Data Service', 'Service de données de la Table des noms de sol', '', '', 'This service provides access to the contents of the Soil Names Table.', 'Ce service donne accès au contenu de la Table des noms de sol.', '2009-10-31 20:00:00', '2012-02-08 17:52:12'),
	(17, 'SLC Version 3.1.1', 'Version 3.1.1 des PPC', '', '', 'SLC v3.1.1 is a minor release that corrects a number of errors found in SLC version 3.1.', 'La version 3.1.1 des PPC est une variante qui corrige un certain nombre d\'erreurs trouvées dans la version 3.1.', '2007-08-26 20:00:00', '2012-02-08 19:17:31'),
	(18, 'SLC Version 3.1', 'Pédo-paysages du Canada, Version 3.1', '', '', 'SLC v3.1 is a replacement for SLC v3.0. Attribute information for 199 agricultural polygons has been added.', 'V3.1 est un remplacement pour la v3.0 des PPC. L\'information sur les attributs de 199 polygones agricoles a été ajouté.', '2006-03-31 19:00:00', '2012-02-08 19:16:47'),
	(19, 'WMS access to the National Ecological Framework', 'Accès WMS pour le Cadre écologique national', '', '', 'This data is part of a nested hierarchy of natural areas.  Digital coverages of ecozones, ecoregions and ecodistricts were compiled on the standard 1:1 million scale map bases of the Soil Landscapes of Canada database.', 'Ces données font partie d\'une hiérarchie imbriquée d\'espaces naturels. Les carte numériques des écozones, écorégions et écodistricts ont été établies sur les fonds de carte au 1/1 000 000 normalisés de la base de données des Pédo-paysages du Canada.', '2005-04-30 20:00:00', '2012-02-08 19:21:49'),
	(20, 'SLC Version 3.0', 'Pédo-paysages du Canada, Version 3.0', '', '', 'SLC v3.0 is a replacement for SLC version 2.2 and provides new soils information at a scale of 1:1 million for the major agricultural regions of Canada.', 'V3.0 des PPC remplace la version 2.2 et offre de nouveaux renseignements à l’échelle de 1 :1 million pour les grandes régions agricoles du Canada.', '2004-10-31 19:00:00', '2013-10-16 20:08:56'),
	(21, 'Revised Plant Hardiness Zones in Canada map', 'Zones de rusticité des plantes au Canada', '', '', 'The Plant Hardiness Zones map outlines the different zones in Canada where various types of trees, shrubs and flowers will most likely survive.', 'La carte des zones de rusticité des plantes montre les zones du Canada qui sont les plus propices à la survie de différents types d\'arbres, d\'arbustes et de fleurs, d\'après les conditions climatiques moyennes de chaque région.', '2001-04-30 20:00:00', '2012-02-08 18:21:06'),
	(22, 'Soil Map Joiner version 1.0', 'Assembleur de carte pédologique v1.0', '', '', 'This document describes the Soil Map Joiner tools for ARC/INFO. This tool facilitates the mapjoining of adjacent detailed soil survey data.', 'Ce document décrit l\'assembleur de carte pédologique (Soil Map Joiner) pour ARC/INFO. Celui-ci facilite l\'assemblage sur la carte des levés pédologiques détaillés adjacents.', '2000-11-30 19:00:00', '2012-02-08 18:56:40'),
	(23, 'Soil Mapper version 1.0', 'Module d\'extension de cartographie pédologique v1.0', '', '', 'This document describes the Soil Mapper extension for ArcView.', 'Le présent document décrit le module d\'extension de cartographie pédologique (Soil Mapper) pour ArcView.', '2000-09-30 20:00:00', '2012-02-08 18:59:25'),
	(24, 'Detailed Soil Surveys: datasets and documentation', 'Levés pédologiques détaillés : documentation et ensembles de données', '/cansis/nsdb/dss/index.html', '/siscan/nsdb/dss/index.html', 'Soil surveys have been published for most of the agricultural areas, and many surrounding areas, across Canada. Data from these surveys comprise the most detailed soil inventory information in the NSDB.', 'Des levés pédologiques ont été publiés pour la plupart des régions agricoles et de nombreuses régions avoisinantes à l\'échelle du Canada. Les données tirées de ces levés renferment l\'information la plus détaillée sur l\'inventaire des sols dans la BNDS.', '2000-03-31 19:00:00', '2012-02-08 15:20:36'),
	(25, 'CLI datasets and documentation', 'Inventaire des terres du Canada (ITC)', '/cansis/nsdb/cli/index.html', '/siscan/nsdb/cli/index.html', 'The Canada Land Inventory is a comprehensive multi-disciplinary land inventory of rural Canada, covering over 2.5 million square kilometers of land and water.', 'L\'Inventaire des terres du Canada est un répertoire complet et pluridisciplinaire des terres rurales du Canada, couvrant plus de 2,5 millions de kilomètres carrés de terres et d\'eau.', '1999-11-30 19:00:00', '2012-02-07 21:51:36'),
	(26, 'Ecostratification datasets - improved documentation', 'Ensembles de données écostratification (documentation améliorée)', '/cansis/nsdb/ecostrat/1999report/data_tables.html', '/siscan/nsdb/ecostrat/1999report/data_tables.html', 'List of Data Tables (in dbase format) with a link to References.', 'Liste des tableaux de données (en format dbase), ainsi qu\'un lien de la page des références.', '1999-11-30 19:00:00', '2012-02-09 14:52:17'),
	(27, 'Crop Heat Units for Eastern Canada', 'Unités thermiques moyennes de croissance du maïs et du soja dans l\'Est du Canada', '', '', 'The Corn Heat Unit system, now also known as Crop Heat Units (CHU), was developed in the 1960\'s and is used to recommend corn hybrids and soybean varieties which are best suited for production in specific CHU zones in various regions of Canada.', 'Le système des unités thermiques maïs (UTM), qu\'on a créé au cours des années 60, sert à recommander les hybrides de maïs et les variétés de soja les mieux adaptés à la production dans certaines zones UTM de diverses régions canadiennes. ', '1999-07-31 20:00:00', '2012-02-09 14:27:47'),
	(28, 'Printed maps now available via the Web', 'Les cartes imprimées sont maintenant disponibles via le Web', '/cansis/publications/maps/index.html', '/siscan/publications/maps/index.html', 'Over the years, CanSIS and its partners have printed thousands of maps about Canadian soils, landscapes, and climate. Many of these maps have now been scanned and are available here.', 'Au fil des ans, SISCan et ses partenaires ont imprimé des milliers de cartes des sols, des paysages et du climat du Canada. Plusieurs de ces cartes ont été numérisées par scanneur et sont disponibles ici.', '1999-06-30 20:00:00', '2012-02-09 15:27:27'),
	(29, 'CanSIS - Printed publications via the Internet', 'SISCan - Publications imprimées accessibles par Internet', '', '', 'CanSIS printed publications are available on the Internet using Adobe\'s PDF technology.', 'Des publications imprimées traitant du CanSIS sont accessibles par Internet au moyen de la technologie PDF d\'Adobe.', '1999-02-28 19:00:00', '2012-02-09 15:50:40'),
	(31, 'Plant Hardiness Zones in Canada', 'Zones De Rusticité Des Plantes au Canada', '', '', '', '', '1998-09-30 20:00:00', '2012-02-09 18:19:43'),
	(32, 'SLC DOMSUB attributes from version 1.1 linked to version 2.2', 'Attributs du fichier DOMSUB des PPC - v1.1 liée à la v2.2', '/cansis/nsdb/slc/v2.2/domsub/index.html', '/siscan/nsdb/slc/v2.2/domsub/index.html', 'The DOMSUB file describes aspects of soil landscapes which had been described in version 1.1, but not carried forward into the Component / Carbon Layer Table structure. ', 'Le fichier DOMSUB décrit les aspects des pédo-paysages qui ont été décrits dans la version 1.1, mais qui n\'ont pas été reportés dans la structure de la table des composantes de couche de carbone. ', '1998-05-31 20:00:00', '2012-02-08 19:02:35'),
	(33, 'Canadian System of Soil Classification 3rd edition (CSSC3) published', 'Le système canadien de classification des sols  (troisième édition)', '/cansis/publications/manuals/1998-cssc-ed3/index.html', '/siscan/publications/manuals/1998-cssc-ed3/index.html', 'This revised publication replaces The Canadian System of Soil Classification published in 1987.', 'Cette publication révisée remplace le Système canadien de classification des sols publiée en 1987.', '1998-03-31 19:00:00', '2012-02-08 19:08:48'),
	(34, 'Component Table Mapper', 'Mappeur de tables de composantes', '', '', 'This document describes the Component Table extension for the ArcView 3.0 graphic user interface.', 'Ce document décrit le module d\'extension de la table des composantes (Component Table Mapper) pour l\'interface graphique (GUI) d\'ArcView 3.0.', '1998-02-28 19:00:00', '2012-02-07 20:43:23'),
	(35, 'ECODISTRICT climate normals', 'Normales climatologiques des ÉCODISTRICTS', '', '', 'Climate normals are available as a GIS database for Ecodistricts in Canada with monthly climatic normals for the 1961-1990 normals period.', 'On peut avoir accès à la base de données des normales climatologiques, présentées en format SIG, de tous les écodistricts du Canada, qui comprend également les normales mensuelles de 1961 à 1990.', '1998-01-31 19:00:00', '2012-02-09 16:13:15'),
	(37, 'PARS version 1.1', 'Aperçu du SRAP v1.1', '', '', 'The potential applications of PARS are many. It is useful wherever information has been mapped based on one set of polygons, and summaries or analyses are desired based on another set of polygons. ', 'Les applications potentielles du SRAP sont multiples. Celui-ci s\'avère utile chaque fois qu\'une information a été cartographiée sur un ensemble de polygones et que l\'on veut obtenir des sommaires ou effectuer des analyses basées sur un autre ensemble de polygones. ', '1997-10-31 19:00:00', '2012-02-09 17:47:55'),
	(38, 'Component Table Mapping Extension', 'Mappeur de tables de composantes', '', '', 'The objective of this tool is to aid in the mapping of multi-record polygon attributes, such as those found in SLC Component Tables.', 'Cet outil a pour objet de faciliter le mappage des enregistrements multiples des attributs de polygones, comme ceux que renferment les tables des composantes des PPC. ', '1997-05-31 20:00:00', '2012-02-09 17:52:12'),
	(39, 'Texture class assignment', 'Classes de détermination de la texture de sol', '', '', 'Determining soil texture from sand, silt, and clay content.', 'Détermination de la texture d\'un sol d\'après les proportions de sable, de limon et d\'argile qu\'il contient.', '1997-05-31 20:00:00', '2012-02-09 17:56:58'),
	(40, 'Introduction to PARS', 'Introduction au SRAP', '', '', '', '', '1997-05-31 20:00:00', '2012-02-09 19:28:48'),
	(42, 'How to rollup SLC Components', 'Agrégation des composantes des PPC', '', '', 'This form will creates an ARC/INFO AML that will summarize SLC components to higher levels in the national ecological framework (i.e. ecodistricts, ecoregions, ecoprovinces, or ecozones).', 'Ce formulaire créera un tableau sommaire en AML (langage de macros Arc) d\'ARC/INFO regroupant les composantes des PPC à des niveaux supérieurs de la hiérarchie du cadre écologique national (c.-à-d. en écorégions, écoprovinces ou écozones). \r\n', '1997-02-28 19:00:00', '2012-02-09 19:06:11'),
	(43, 'SLC version 2.2', 'Version 2.2 des PPC', '/cansis/nsdb/slc/v2.2/index.html', '/siscan/nsdb/slc/v2.2/index.html', 'This version includes some significant changes to the quality of the data and the structure of the dataset. ', 'Cette version comprend des changements importants apportés pour améliorer la qualité des données et la structure de la série de données. ', '1996-11-30 19:00:00', '2012-02-09 19:10:44'),
	(44, 'CanSIS starts serving soil science data and documentation via HTTP', 'SISCan commence à offrir des données et documentation des sciences du sol via le protocole HTTP', '', '', 'Canadian Soil Information Service', 'Service d\'information sur les sols du Canada', '1994-08-10 20:00:00', '2012-03-30 17:00:15'),
	(45, 'Soil Degradation Risk scanned maps', 'Cartes de risque de dégradation des sols', '/cansis/publications/maps/index.html', '/siscan/publications/maps/index.html', 'Soil degradation risk maps showing wind erosion risk, water erosion risk, and risk of salinization. These maps were produced by Agriculture Canada between 1986 and 1993, based on climate information tied to SLC polygons.', 'Ces cartes montrant le risque d\'érosion éolienne, le risque d\'érosion de l\'eau, et le risque de salinisation. Ils ont été produites par Agriculture Canada entre 1986 et 1993, basée sur les polygones PPC.', '2012-06-27 17:27:02', '2012-06-27 17:27:02'),
	(47, 'CanSIS starts to distribute soil data via FTP', 'SISCan commence à distribuer des données sur les sols via FTP', '', '', 'CanSIS starts to distribute soil data via FTP', 'SISCan commence à distribuer des données sur les sols via FTP', '1993-01-05 14:48:13', '2013-06-27 14:48:13'),
	(48, 'First web maps', 'Premières cartes du Web', '', '', 'CanSIS develops the first distributed web mapping application', 'CanSIS développe la première application distribué de cartes du Web.', '1995-05-05 14:48:13', '2013-06-27 14:48:13'),
	(50, 'Northern Land Use scanned maps', 'Cartes de l\'utilisation des terres nordiques', '/cansis/publications/maps/nluis/250k/lu/index.html', '/siscan/publications/maps/nluis/250k/lu/index.html', 'These maps present the major environmental and social features of land use for most of northern Canada between the latitudes of 60 and 77 degrees north.', 'Ces cartes présentent les principales caractéristiques environnementales et sociales de l\'utilisation des terres pour la plupart du nord du Canada entre les latitudes 60 et 77 degrés de latitude nord.', '2012-10-25 17:27:02', '2012-06-27 17:27:02'),
	(51, 'Soil survey index maps in KML format', 'Cartes d\'indexes pour les études pédologiques, au format KML', '/cansis/publications/surveys/index.html', '/cansis/publications/surveys/index.html', '', '', '2012-12-05 14:48:13', '2013-07-11 15:55:13'),
	(52, 'Improved Detailed Soil Survey Compilations', 'Amélioration des compilations de levés pédologiques détaillés', '/cansis/nsdb/dss/v3/index.html', '/siscan/nsdb/dss/v3/index.html', 'The database structures have been updated.', 'Les structures de base de données ont été mises à jour.', '2013-07-11 15:19:04', '2013-07-11 15:38:19'),
	(53, 'Detailed Soil Survey Compilation for the Yukon Territory', 'Compilation de levés pédologiques détaillés pour le territoire du Yukon', '/cansis/nsdb/dss/v3/index.html', '/siscan/nsdb/dss/v3/index.html', '', '', '2013-07-11 15:24:33', '2013-07-11 15:57:09'),
	(55, 'Updated attribute data for the Ontario Detailed Soil Survey', ' Données mises à jour pour les levés pédologiques détaillés de l\'Ontario', '/cansis/nsdb/dss/v3/index.html', '/siscan/nsdb/dss/v3/index.html', 'Updates were made to the CMP, SNT, and SLT.  The geometry remains unchanged.', 'Mises à jour ont été apportées à la CMP, SNT, et SLT. La géométrie n\'a pas changé.', '2014-02-03 22:16:03', '2014-02-03 22:19:41'),
	(56, 'Soil Survey Report for Napierville', 'Étude pédologique du comté de Napierville', '/cansis/publications/surveys/pq/pq42b/index.html', '/siscan/publications/surveys/pq/pq42b/index.html', '', '', '2014-03-12 14:49:40', '2014-03-12 14:52:03'),
	(57, 'Updated attribute data for the Alberta Detailed Soil Survey', 'Données mises à jour pour les levés pédologiques détaillés de l\'Alberta', '/cansis/nsdb/dss/v3/index.html', '/siscan/nsdb/dss/v3/index.html', 'Updates were made to the geometry and all data files.', 'Mises à jour ont été apportées à la géométrie et à toutes les données.', '2014-05-29 20:07:55', '2014-05-29 20:07:55'),
	(58, 'Updated attribute data for the Manitoba Detailed Soil Survey', 'Données mises à jour pour les levés pédologiques détaillés de Manitoba', '/cansis/nsdb/dss/v3/index.html', '/siscan/nsdb/dss/v3/index.html', 'Updates were made to the geometry and all data files.', 'Mises à jour ont été apportées à la géométrie et à toutes les données.', '2016-01-27 21:18:10', '2016-01-27 21:18:10');
