	(18, '', 'contact', 1, '2018-07-24', 'meta', 'Contact CanSIS', 'Contact CanSIS', '<div>\r\n	<br>\r\n	<strong>Canadian Soil Information Service</strong> (CanSIS)<br/>\r\n	Room 1135, K.W. Neatby Building<br>\r\n	960 Carling Ave.<br>\r\n	Ottawa, Ontario, Canada<br>\r\n	K1A 0C6<br>\r\n</div>\r\n<br>\r\n<div>\r\n	<p><span class="glyphicon glyphicon-envelope" aria-hidden="true"></span>  Email: <a title="Email: Canadian Soil Information Service" href="mailto:aafc.cansis-siscan.aac@canada.ca">aafc.cansis-siscan.aac@canada.ca</a></p>\r\n	<p><span class="glyphicon glyphicon-phone-alt" aria-hidden="true"></span>  Toll-free: 1-866-367-8506</p>\r\n	<p><span class="glyphicon glyphicon-print" aria-hidden="true"></span>  Fax: 613-759-1937</p>\r\n</div>\r\n\r\n<div>\r\n<h3>Service standards</h3>\r\n<p>Our goal is to achieve our service standards a minimum of 80% of the time under normal circumstances.</p>\r\n	<ul>\r\n		<li>Provide an estimated delivery date for information products within 3 business days.</li>\r\n	</ul>\r\n</div>\r\n<div>\r\n	<br>\r\n	<a href="http://www.agr.gc.ca/pscontact-contactezps/index_e.php"><img src = "/cansis/images/feedback-en.jpg" alt ="Submit your feedback"><br>Submit your feedback</a>\r\n</div>\r\n\r\n\r\n\r\n', '2018-07-24', 'meta', 'Contactez SISCan', 'Contactez SISCan', '<div>\r\n	<br/>\r\n	<strong>Service d\'information sur les sols du Canada</strong>\r\n	(SISCan)<br />\r\n	Édifice K.W. Neatby<br />\r\n	960 Carling Ave<br />\r\n	Ottawa, Ontario, Canada<br />\r\n	K1A 0C6<br/>\r\n</div>\r\n</br>\r\n<div>\r\n<p><span class="glyphicon glyphicon-envelope" aria-hidden="true"></span>  Courriel: <a title="Courriel : Service d\'informationsur les sols du Canada" href="mailto:aafc.cansis-siscan.aac@canada.ca">aafc.cansis-siscan.aac@canada.ca</a></p>\r\n</p><span class="glyphicon glyphicon-phone-alt" aria-hidden="true"></span>  Téléphone: 1-866-367-8506</p>\r\n<p><span class="glyphicon glyphicon-print" aria-hidden="true"></span>  Facsimile: 613-759-1937</p>\r\n</div>\r\n<div>\r\n<h3>Normes de service</h3>\r\n<p>Notre objectif est de parvenir à nos normes de service un minimum de 80 % du temps, sous des circonstances normales.</p>\r\n<ul>\r\n	<li>Donner une date estimative de livraison dans un délai de trois jours ouvrables pour les produits d\'information.</li>\r\n</ul>\r\n</div>\r\n<div>\r\n	<br>\r\n	<a href="http://www.agr.gc.ca/pscontact-contactezps/index_f.php"><img src = "/cansis/images/feedback-fr.jpg" alt ="Soumettre votre rétroaction"><br>Soumettre votre rétroaction</a>\r\n</div>\r\n\r\n', '2018-07-24', 'Migrate to canada.ca'),
	(19, '/glossary', 'table-2', 1, '2013-05-10', 'meta', 'Table 2. Coarse fragments', 'Table 2. Coarse fragments', '<table>\r\n<thead>\r\n<tr>\r\n<th id="a01" class="alignLeft">Shape and kind of fragments</th>\r\n<th id="a02" class="alignLeft">Up to 7.5 <abbr title="centimetres">cm</abbr> (3 inches) in diameter</th>\r\n<th id="a03" class="alignLeft">7.5-25 <abbr title="centimetres">cm</abbr> in diameter</th>\r\n<th id="a04" class="alignLeft">Over 25 <abbr title="centimetres">cm</abbr> (10 inches) in diameter</th>\r\n</tr>\r\n</thead>\r\n<tfoot>\r\n<tr>\r\n<td colspan="4">\r\n[1] The individual classes are not always differentiating characteristics of mapping units.<br />\r\n[2] Bouldery is sometimes used where stones are larger than 24 inches.<br />\r\n[3] Formerly called "stony."\r\n</td>\r\n</tr>\r\n</tfoot>\r\n<tbody>\r\n<tr>\r\n<th id="b01" headers="a01" class="alignLeft">Rounded and subrounded fragments<br />All kinds of rock</th>\r\n<td headers="a02 b01">Gravelly [1]</td>\r\n<td headers="a03 b01">Cobbly</td>\r\n<td headers="a04 b01">Stony (or bouldery)[2]</td>\r\n</tr>\r\n<tr>\r\n<th id="b02" headers="a01" class="alignLeft">Irregularly shaped angular fragments Chert</th>\r\n<td headers="a02 b02">Cherty</td>\r\n<td headers="a03 b02">Coarse cherty</td>\r\n<td headers="a04 b02">Stony</td>\r\n</tr>\r\n<tr>\r\n<th id="b03" headers="a01" class="alignLeft">Irregularly shaped angular fragments Other than chert</th>\r\n<td headers="a02 b03">Angular gravelly</td>\r\n<td headers="a03 b03">Angular cobbly [3]</td>\r\n<td headers="a04 b03">Stony</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n\r\n<br />\r\n\r\n<table>\r\n<thead>\r\n<tr>\r\n<th id="c01" class="alignLeft">Shape and kind of fragments</th>\r\n<th id="c02" class="alignLeft">Up to 15 <abbr title="centimetres">cm</abbr> (6 inches) in length</th>\r\n<th id="c03" class="alignLeft">15-38 <abbr title="centimetres">cm</abbr> (6-15 inches) in length</th>\r\n<th id="c04" class="alignLeft">Over 38 <abbr title="centimetres">cm</abbr> (15 inches) in length</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<th id="d01" headers="c01" class="alignLeft">Thin flat fragments<br />Thin flat sandstone,limestone,schist</th>\r\n<td headers="c02 d01">Channery</td>\r\n<td headers="c03 d01">Flaggy</td>\r\n<td headers="c04 d01">Stony</td>\r\n</tr>\r\n<tr>\r\n<th id="d02" headers="c01" class="alignLeft">Thin flat fragments<br />Slate</th>\r\n<td headers="c02 d02">Slaty</td>\r\n<td headers="c03 d02">Flaggy</td>\r\n<td headers="c04 d02">Stony</td>\r\n</tr>\r\n<tr>\r\n<th id="d03" headers="c01" class="alignLeft">Thin flat fragments<br />Shale</th>\r\n<td headers="c02 d03">Shaly</td>\r\n<td headers="c03 d03">Flaggy</td>\r\n<td headers="c04 d03">Stony</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n', '2013-05-10', 'meta', 'Tableau 2. Fragments grossiers', 'Tableau 2. Fragments grossiers', '<table>\r\n<thead>\r\n<tr>\r\n<th id="a01" class="alignLeft">Forme et type de fragments</th>\r\n<th id="a02" class="alignLeft">Jusqu\'à 7.5 <abbr title="centimètres">cm</abbr> (3 <abbr title="pouces">po</abbr>) de diamètre</th>\r\n<th id="a03" class="alignLeft">7.5 à 25 <abbr title="centimètres">cm</abbr> (3 à 10 <abbr title="pouces">po</abbr>) de diamètre</th>\r\n<th id="a04" class="alignLeft">Plus de 25 <abbr title="centimètres">cm</abbr> (10 <abbr title="pouces">po</abbr>) de diamètre</th>\r\n</tr>\r\n</thead>\r\n<tfoot>\r\n<tr>\r\n<td colspan="4">\r\n[1] Les classes ne constituent pas toujours des critères de diffèrenciation pour la cartographie.<br />\r\n[2] Blocailleux désignent parfois des pierres de plus de 24 <abbr title="pouces">po</abbr> (69 <abbr title="centimètres">cm</abbr>) de grosseur.<br />\r\n[3] Anciennement: pierreux.\r\n</td>\r\n</tr>\r\n</tfoot>\r\n<tbody>\r\n<tr>\r\n<th id="b01" headers="a01" class="alignLeft">Fragments arrondis et subarrondis<br />toutes sortes deroches</th>\r\n<td headers="a02 b01">graveleux [1]</td>\r\n<td headers="a03 b01">caillouteux<br />(ou en galets)</td>\r\n<td headers="a04 b01">Pierreus<br />(ou blocailleux)[2]</td>\r\n</tr>\r\n<tr>\r\n<th id="b02" headers="a01" class="alignLeft">Fragments anguleux de forme irrégulière chert</th>\r\n<td headers="a02 b02">cherteux</td>\r\n<td headers="a03 b02">en gros fragments de chert</td>\r\n<td headers="a04 b02">pierreux</td>\r\n</tr>\r\n<tr>\r\n<th id="b03" headers="a01" class="alignLeft">Fragments anguleux de forme irrégulière autres que le chert</th>\r\n<td headers="a02 b03">graveleux (anguleux)</td>\r\n<td headers="a03 b03">caillouteux (anguleux)</td>\r\n<td headers="a04 b03">pierreus</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n\r\n<br />\r\n\r\n<table>\r\n<thead>\r\n<tr>\r\n<th id="c01" class="alignLeft">Forme et type de fragments</th>\r\n<th id="c02" class="alignLeft">Jusqu\'à 15 <abbr title="centimètres">cm</abbr> (6 <abbr title="pouces">po</abbr>) de longueur</th>\r\n<th id="c03" class="alignLeft">15 à 38 <abbr title="centimètres">cm</abbr> (6 à 15 <abbr title="pouces">po</abbr>) de longueur</th>\r\n<th id="c04" class="alignLeft">Plus de 38 <abbr title="centimètres">cm</abbr> (15 <abbr title="pouces">po</abbr>) de longueur</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<th id="d01" headers="c01" class="alignLeft">Fragments minces et plats<br />grès, calcaire et schiste</th>\r\n<td headers="c02 d01">en plaquettes</td>\r\n<td headers="c03 d01">en dalles</td>\r\n<td headers="c04 d01">pierreux </td>\r\n</tr>\r\n<tr>\r\n<th id="d02" headers="c01" class="alignLeft">Fragments minces et plats<br />ardoise</th>\r\n<td headers="c02 d02">ardoisier</td>\r\n<td headers="c03 d02">en dalles</td>\r\n<td headers="c04 d02">pierreux</td>\r\n</tr>\r\n<tr>\r\n<th id="d03" headers="c01" class="alignLeft">Fragments minces et plats<br />schiste argileux</th>\r\n<td headers="c02 d03">feuilleté</td>\r\n<td headers="c03 d03">en dalles</td>\r\n<td headers="c04 d03">pierreux</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n', '2013-05-10', 'Migrate to publications.gc.ca'),
	(20, '/glossary', 'table-4', 1, '2013-04-09', 'meta', 'table 4. Soil water', 'table 4. Soil water', '<table>\r\n<thead>\r\n<tr>\r\n<th scope="col">Term</th>\r\n<th scope="col">Symbol (a)</th>\r\n<th scope="col"> Dimension</th>\r\n<th scope="col">Unit</th>\r\n</tr>\r\n</thead>\r\n<tfoot>\r\n<tr>\r\n<td colspan="4">\r\n(a) Symbols such as C, K, and D may have w or h as a subscript if in the same paper they are used for water as well as For heat.<br />\r\n(b) The dimension depends on the units used to describe the driving force, as in the following table.\r\n</td>\r\n</tr>\r\n</tfoot>\r\n<tbody>\r\n<tr>\r\n<td>Total potential</td>\r\n<td><img src="y-symbol.gif" alt="y-symbol"/></td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="gram">g</abbr>&#173;&#185;, joule <abbr title="kilograms">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Osmotic potential</td>\r\n<td>O</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="gram">g</abbr>&#173;&#185;, joule <abbr title="kilograms">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Gravitational potential</td>\r\n<td>Z</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="gram">g</abbr>&#173;&#185;, joule <abbr title="kilograms">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Matric potential</td>\r\n<td>M</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="gram">g</abbr>&#173;&#185;, joule <abbr title="kilograms">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Soil water pressure</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#178;</td>\r\n<td>dyne <abbr title="centimetres">cm</abbr>&#173;&#178;, bar, <abbr title="centimetres">cm</abbr> water, <abbr title="centimetres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Osmotic pressure</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#185;</td>\r\n<td>dyne <abbr title="centimetres">cm</abbr>&#173;&#178;, bar, <abbr title="centimetres">cm</abbr> water, <abbr title="centimetres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Total pressure</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#178;</td>\r\n<td>dyne <abbr title="centimetres">cm</abbr>&#173;&#178;, bar, <abbr title="centimetres">cm</abbr> water, <abbr title="centimetres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Hydraulic head</td>\r\n<td>H</td>\r\n<td>L</td>\r\n<td><abbr title="centimetres">cm</abbr>, <abbr title="metre">m</abbr></td>\r\n</tr>\r\n<tr>\r\n<td>Hydraulic potential</td>\r\n<td>&#216;</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="gram">g</abbr>&#173;&#185;, joule <abbr title="kilograms">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Water content</td>\r\n<td>w</td>\r\n<td>-</td>\r\n<td><abbr title="centimetres">cm</abbr>&#179; <abbr title="centimetres">cm</abbr>&#173;&#179;, <abbr title="gram">g</abbr> <abbr title="gram">g</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Differential water capacity</td>\r\n<td>C</td>\r\n<td>M&#173;&#185;LT&#178;</td>\r\n<td><abbr title="centimetres">cm</abbr>&#178; dyne&#173;&#185;, bar&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Hydraulic conductivity</td>\r\n<td>K</td>\r\n<td>(b)</td>\r\n<td>(b)</td>\r\n</tr>\r\n<tr>\r\n<td>Permeability</td>\r\n<td>k</td>\r\n<td> L&#178;</td>\r\n<td><abbr title="centimetres">cm</abbr>&#178;, Darcy</td>\r\n</tr>\r\n<tr>\r\n<td>Soilwater diffusivity</td>\r\n<td>D</td>\r\n<td>L&#178;T&#173;&#185;</td>\r\n<td><abbr title="centimetres">cm</abbr>&#178;sec&#173;&#185;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n\r\n<br />\r\n\r\n<table>\r\n<tr>\r\n<th id="a01">Driving force</th>\r\n<th id="a02">Dimension</th>\r\n<th id="a03" colspan=2>Hydraulic conductivity</th>\r\n</tr>\r\n<tr>\r\n<th>&#160;</th>\r\n<th>&#160;</th>\r\n<th id="b01" headers="a03">Dimension</th>\r\n<th id="b02" headers="a03">Unit</th>\r\n</tr>\r\n<tr>\r\n<td headers="a01">Hydraulic potential gradient</td>\r\n<td headers="a02">LT&#173;&#178;</td>\r\n<td headers="a03 b01">T</td>\r\n<td headers="a03 b02">sec\r\n</tr>\r\n<tr>\r\n<td headers="a01">Hydraulic head gradient</td>\r\n<td headers="a02">LL&#173;&#185;</td>\r\n<td headers="a03 b01">LT&#173;&#185;</td>\r\n<td headers="a03 b02"><abbr title="centimetres">cm</abbr> sec&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td headers="a01">Pressure gradient</td>\r\n<td headers="a02">ML&#173;&#178;T&#173;&#178;</td>\r\n<td headers="a03 b01">M&#173;&#185;L&#179;T</td>\r\n<td headers="a03 b02">g&#173;&#185; <abbr title="centimetres">cm</abbr>&#179; sec</td>\r\n</tr>\r\n</table>\r\n', '2013-04-09', 'meta', 'tableau 4. Eau du sol', 'tableau 4. Eau du sol', '<table>\r\n<thead>\r\n<tr>\r\n<th scope="col">Terme</th>\r\n<th scope="col">Symbole (a)</th>\r\n<th scope="col"> Dimensions</th>\r\n<th scope="col">Unités</th>\r\n</tr>\r\n</thead>\r\n<tfoot>\r\n<tr>\r\n<td colspan="4">\r\n(a)Les symboles comme C, K et D peuvent être accomagnée d\'un indice, w ou h. si dans un même texte ils sont utilisés en relation et avec l\'eau et avec la chaleur.<br />\r\n(b)Les dimensions sont fonction des unités employées pur décrire la force de pénétration, comme l\'indique le tableau suvant.\r\n</td>\r\n</tr>\r\n</tfoot>\r\n<tbody>\r\n<tr>\r\n<td>Potentiel total </td>\r\n<td><img src="y-symbol.gif" alt="y-symbole" /></td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="grammes">g</abbr>&#173;&#185;, joule <abbr title="kilogrammes">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Potentiel osmotique </td>\r\n<td>O</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="grammes">g</abbr>&#173;&#185;, joule <abbr title="kilogrammes">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Potentiel de gravité</td>\r\n<td>Z</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="grammes">g</abbr>&#173;&#185;, joule <abbr title="kilogrammes">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Potential capillaire (de matrice) </td>\r\n<td>M</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="grammes">g</abbr>&#173;&#185;, joule <abbr title="kilogrammes">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Pression de l\'eau du sol</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#178;</td>\r\n<td>dyne <abbr title="centimètres">cm</abbr>&#173;&#178;, bar, <abbr title="centimètres">cm</abbr> water, <abbr title="centimètres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Pression osmotique</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#185;</td>\r\n<td>dyne <abbr title="centimètres">cm</abbr>&#173;&#178;, bar, <abbr title="centimètres">cm</abbr> water, <abbr title="centimètres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Pression totale</td>\r\n<td>-</td>\r\n<td>ML&#173;&#185;T&#173;&#178;</td>\r\n<td>dyne <abbr title="centimètres">cm</abbr>&#173;&#178;, bar, <abbr title="centimètres">cm</abbr> water, <abbr title="centimètres">cm</abbr> Hg</td>\r\n</tr>\r\n<tr>\r\n<td>Charge hydraulique</td>\r\n<td>H</td>\r\n<td>L</td>\r\n<td><abbr title="centimètres">cm</abbr>, <abbr title="mètre">m</abbr></td>\r\n</tr>\r\n<tr>\r\n<td>Potentiel hydraulique</td>\r\n<td>&#216;</td>\r\n<td>L&#178;T&#173;&#178;</td>\r\n<td>erg <abbr title="grammes">g</abbr>&#173;&#185;, joule <abbr title="kilogrammes">kg</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Teneur en eau</td>\r\n<td>w</td>\r\n<td>-</td>\r\n<td><abbr title="centimètres">cm</abbr>&#179; <abbr title="centimètres">cm</abbr>&#173;&#179;, <abbr title="grammes">g</abbr> <abbr title="grammes">g</abbr>&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Capacité de rétention différentielle</td>\r\n<td>C</td>\r\n<td>M&#173;&#185;LT&#178;</td>\r\n<td><abbr title="centimètres">cm</abbr>&#178; dyne&#173;&#185;, bar&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td>Conductivité hydraulique</td>\r\n<td>K</td>\r\n<td>(b)</td>\r\n<td>(b)</td>\r\n</tr>\r\n<tr>\r\n<td>Perméabilité</td>\r\n<td>k</td>\r\n<td> L&#178;</td>\r\n<td><abbr title="centimètres">cm</abbr>&#178;, Darcy</td>\r\n</tr>\r\n<tr>\r\n<td>Capacité de diffusion</td>\r\n<td>D</td>\r\n<td>L&#178;T&#173;&#185;</td>\r\n<td><abbr title="centimètres">cm</abbr>&#178;sec&#173;&#185;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n\r\n<br />\r\n\r\n<table>\r\n<tr>\r\n<th id="a01">Force de pénétration</th>\r\n<th id="a02">Dimensions</th>\r\n<th id="a03" colspan=2> Conductivité hydraulique </th>\r\n</tr>\r\n<tr>\r\n<th>&#160;</th>\r\n<th>&#160;</th>\r\n<th id="b01" headers="a03">Dimensions</th>\r\n<th id="b02" headers="a03">Unités</th>\r\n</tr>\r\n<tr>\r\n<td headers="a01">Gradient de potentiel hydraulique</td>\r\n<td headers="a02">LT&#173;&#178;</td>\r\n<td headers="a03 b01">T</td>\r\n<td headers="a03 b02">sec\r\n</tr>\r\n<tr>\r\n<td headers="a01">Gradient de charge hydraulique</td>\r\n<td headers="a02">LL&#173;&#185;</td>\r\n<td headers="a03 b01">LT&#173;&#185;</td>\r\n<td headers="a03 b02"><abbr title="centimètres">cm</abbr> sec&#173;&#185;</td>\r\n</tr>\r\n<tr>\r\n<td headers="a01">Gradient de pression</td>\r\n<td headers="a02">ML&#173;&#178;T&#173;&#178;</td>\r\n<td headers="a03 b01">M&#173;&#185;L&#179;T</td>\r\n<td headers="a03 b02">g&#173;&#185; <abbr title="centimètres">cm</abbr>&#179; sec</td>\r\n</tr>\r\n</table>\r\n', '2013-04-09', 'Migrate to publications.gc.ca'),
	(21, '/glossary', 'table-3', 1, '2013-04-09', 'meta', 'Table 3. Soil structure', 'Table 3. Soil structure', '<table border="1">\r\n<thead>\r\n<tr>\r\n<th id="a1.1">Type</th>\r\n<th id="a1.2">Kind</th>\r\n<th id="a1.3">Class</th>\r\n<th id="a1.4">Size(<abbr title="millimetres">mm</abbr>)</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<th class="alignLeft" rowspan="2" headers="a1.1" id="a2.1">1. Structureless - no observable aggregation or no definite orderly arrangement around natural lines of weakness.</th>\r\n<td headers="a1.2 a2.1">A. Single grain - loose, incoherent mass of individual particles as in sands.</td>\r\n<td>&#160;</td>\r\n<td>&#160;</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.2 a2.1">B. Amorphous (massive) - a coherent mass showing no evidence of any distinct arrangement of soil particles.</td>\r\n<td>&#160;</td>\r\n<td>&#160;</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="11" headers="a1.1" id="a4.1">2. Blocklike-soil particles arranged around a point and bounded by flat or rounded surfaces.</th>\r\n<td rowspan="4" headers="a1.2 a4.1">A. Blocky (angular blocky)- rectangular and flattened, vertices sharply angular</td>\r\n<td headers="a1.3 a4.1">Fine blocky,</td>\r\n<td headers="a1.4 a4.1">&lt;10</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Medium blocky,</td>\r\n<td headers="a1.4 a4.1">10-20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Coarse blocky,</td>\r\n<td headers="a1.4 a4.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Very coarse blocky</td>\r\n<td headers="a1.4 a4.1">&gt;50</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="4" headers="a1.2 a4.1">B. Subangular blocky-faces subrectangular, vertices mostly oblique, or subrounded.</td>\r\n<td headers="a1.3 a4.1">Fine subangular blocky,</td>\r\n<td headers="a1.4 a4.1">&lt;10</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Medium subangular blocky,</td>\r\n<td headers="a1.4 a4.1">10-20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Coarse subangular blocky,</td>\r\n<td headers="a1.4 a4.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Very coarse subangular blocky</td>\r\n<td headers="a1.4 a4.1">&gt;50</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="3" headers="a1.2 a4.1">C. Granular-spheroidal-characterized by rounded vertices.</td>\r\n<td headers="a1.3 a4.1">Fine granular,</td>\r\n<td headers="a1.4 a4.1">&lt;2</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Medium granular,</td>\r\n<td headers="a1.4 a4.1">2-5</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Coarse granular,</td>\r\n<td headers="a1.4 a4.1">5-10</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="3" headers="a1.1" id="a15.1">3. Platelike-soil particles arranged around a horizontal plane and generally bounded by relatively flat horizontal surfaces.</th>\r\n<td rowspan="3" headers="a1.2 a15.1">A. Platy-horizontal planes more or less developed.</td>\r\n<td headers="a1.3 a15.1">Fine platy,</td>\r\n<td headers="a1.4 a15.1">&lt;2</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a15.1">Medium platy,</td>\r\n<td headers="a1.4 a15.1">2-5</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a15.1">Coarse platy,</td>\r\n<td headers="a1.4 a15.1">&gt;5</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="8" headers="a1.1" id="a18.1">4. Prismlike-soil particles arranged around a vertical axis and bounded by relatively flat vertical surfaces.</th>\r\n<td rowspan="4" headers="a1.2 a18.1">A. Prismatic-vertical faces Fine well defined and edges sharp.</td>\r\n<td headers="a1.3 a18.1">Fine prismatic,</td>\r\n<td headers="a1.4 a18.1">&lt;20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Medium prismatic,</td>\r\n<td headers="a1.4 a18.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Coarse prismatic,</td>\r\n<td headers="a1.4 a18.1">50-100</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Very Coarse prismatic,</td>\r\n<td headers="a1.4 a18.1">&gt;100</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="4" headers="a1.2 a18.1">B. Columnar-vertical edges near top of columns not sharp. (Columns may be flat-topped round-topped, or irregular).</td>\r\n<td headers="a1.3 a18.1">Fine columnar,</td>\r\n<td headers="a1.4 a18.1">&lt;20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Medium columnar,</td>\r\n<td headers="a1.4 a18.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Coarse columnar,</td>\r\n<td headers="a1.4 a18.1">50-100</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Very Coarse columnar,</td>\r\n<td headers="a1.4 a18.1">&gt;100</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n', '2013-04-09', 'meta', 'Tableau 3. Structure du sol', 'Tableau 3. Structure du sol', '<table border="1">\r\n<thead>\r\n<tr>\r\n<th id="a1.1">Type</th>\r\n<th id="a1.2">Sous-type</th>\r\n<th id="a1.3">Classe</th>\r\n<th id="a1.4">Grosseur(<abbr title="millimètres">mm</abbr>)</th>\r\n</tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<th class="alignLeft" rowspan="2" headers="a1.1" id="a2.1">1. Sans structure: aucune agrégation visible ni disposition ordonnée et définie autour des lignes naturelles de faible résistance.</th>\r\n<td headers="a1.2 a2.1">A. Particulaire: masse meuble et non cohésive de particules isolées, comme les sables.</td>\r\n<td>&#160;</td>\r\n<td>&#160;</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.2 a2.1">B. Amorphe (massif): masse cohésive ne présentant aucun signe d\'une disposition définie des particules.</td>\r\n<td>&#160;</td>\r\n<td>&#160;</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="11" headers="a1.1" id="a4.1">2. Polyédrique: particules disposées autour d\'un point et délimitées par des surfaces planes ou arondies.</th>\r\n<td rowspan="4" headers="a1.2 a4.1">A. Polyédrique (polyédrique angulaire):faces rectangulaires et aplaties, à ar&ecirc;tes très aigu&euml;s.</td>\r\n<td headers="a1.3 a4.1">Polyédrique fine,</td>\r\n<td headers="a1.4 a4.1">&lt;10</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique moyenne,</td>\r\n<td headers="a1.4 a4.1">10-20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique grossière,</td>\r\n<td headers="a1.4 a4.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique très grossière,</td>\r\n<td headers="a1.4 a4.1">&gt;50</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="4" headers="a1.2 a4.1">B. Polyédrique subangulaire: faces subrectangulaires à ar&ecirc;tes surtout obliques ou subarrondies.</td>\r\n<td headers="a1.3 a4.1">Polyédrique subangulaire fine,</td>\r\n<td headers="a1.4 a4.1">&lt;10</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique subangulaire moyenne,</td>\r\n<td headers="a1.4 a4.1">10-20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique subangulaire grossière,</td>\r\n<td headers="a1.4 a4.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Polyédrique subangulaire très grossière</td>\r\n<td headers="a1.4 a4.1">&gt;50</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="3" headers="a1.2 a4.1">C. Granulaire-sphéro&iuml;dale à ar&ecirc;tes arrondies.</td>\r\n<td headers="a1.3 a4.1">Granulaire fine,</td>\r\n<td headers="a1.4 a4.1">&lt;2</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Granulaire moyenne,</td>\r\n<td headers="a1.4 a4.1">2-5</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a4.1">Granulaire grossière,</td>\r\n<td headers="a1.4 a4.1">5-10</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="3" headers="a1.1" id="a15.1">3. Lamellaire: particules disposées sur un plan horizontal et généralement délimitées par des surfaces relativement planes et horizontales.</th>\r\n<td rowspan="3" headers="a1.2 a15.1">A. Lamellaire: plans horizontaux plus ou moins formés.</td>\r\n<td headers="a1.3 a15.1">Lamellaire fine,</td>\r\n<td headers="a1.4 a15.1">&lt;2</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a15.1">Lamellaire moyenne,</td>\r\n<td headers="a1.4 a15.1">2-5</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a15.1">Lamellaire grossière,</td>\r\n<td headers="a1.4 a15.1">&gt;5</td>\r\n</tr>\r\n<tr>\r\n<th class="alignLeft" rowspan="8" headers="a1.1" id="a18.1">4. Prismatique: particules disposées autour d\'un axe vertical et délimitées par des surfaces relativement planes et verticales.</th>\r\n<td rowspan="4" headers="a1.2 a18.1">A. Prismatique: faces verticales bien définies et ar&ecirc;tes aigu&euml;s.</td>\r\n<td headers="a1.3 a18.1">Prismatique fine,</td>\r\n<td headers="a1.4 a18.1">&lt;20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Prismatique moyenne,</td>\r\n<td headers="a1.4 a18.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Prismatique grossière,</td>\r\n<td headers="a1.4 a18.1">50-100</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Prismatique très grossière,</td>\r\n<td headers="a1.4 a18.1">&gt;100</td>\r\n</tr>\r\n<tr>\r\n<td rowspan="4" headers="a1.2 a18.1">B. Colonnaire: les ar&ecirc;tes verticales près du sommet ne sont pas aigu&euml;s. Les colonnes peuvent avoir un sommet plat, arrondi ou irrégulier.</td>\r\n<td headers="a1.3 a18.1">Colonnaire fine,</td>\r\n<td headers="a1.4 a18.1">&lt;20</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Colonnaire moyenne,</td>\r\n<td headers="a1.4 a18.1">20-50</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Colonnaire grossière,</td>\r\n<td headers="a1.4 a18.1">50-100</td>\r\n</tr>\r\n<tr>\r\n<td headers="a1.3 a18.1">Colonnaire très grossière,</td>\r\n<td headers="a1.4 a18.1">&gt;100</td>\r\n</tr>\r\n</table>\r\n', '2013-04-09', 'Migrate to publications.gc.ca'),
	(24, '/interpretations', 'index', 1, '2016-04-26', 'meta', 'Soil Interpretations and Derived Map Products', 'Soil Interpretations and Derived Map Products', '<p>Interpretive products combine several pieces of information to answer specific questions.  Soil and landscape data can be combined with climate, geology and land use data to address a wide variety of agro-environmental issues. Examples include:</p>\r\n\r\n\r\n<h2>Agri-Environmental Indicators</h2>\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.arcgis.com/home/webmap/viewer.html?url=http%3A%2F%2Fwww.agr.gc.ca%2Fatlas%2Frest%2Fservices%2Fmapservices%2Faafc_agri_environmental_indicators%2FMapServer&source=sd"><img src="/cansis/interpretations/indicators_map.gif" alt="" class="image-left blockSpacerRight" /></br>View interactive map</a>\r\n	</div>\r\n</div>\r\n\r\n	</br>\r\n		<p>The <a href="http://www5.agr.gc.ca/resources/prod/doc/env/naharp-pnarsa/pdf/naharp-pnarsa_e.pdf">National Agri-Environmental Health Analysis and Reporting Program (NAHARP)</a> assesses and reports on the agriculture sector\'s environmental performance via a set of Agri-Environmental Indicators (AEI). These indicators are intended to provide reliable, science-based information on the current state and changes in the conditions of the environment in agriculture at a national or regional&#160;scale.</p>\r\n\r\n\r\n<h2>Canada Land Inventory (CLI) - Soil Capability for Agriculture</h2>\r\n\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.agr.gc.ca/atlas/agpv?webmap-en=5b54c00685b74cab960a54bc444fc927&webmap-fr=79aa06a083fa4b6e8f65e4828ee5a746"><img src="/cansis/interpretations/cli_map.gif" alt="" class="image-left blockSpacerRight" /></br>View interactive map</a>\r\n	</div>\r\n</div>\r\n\r\n</br>\r\n	<p>The Canada Land Inventory (CLI) is a comprehensive multi-disciplinary land inventory of rural Canada, covering over 2.5 million square kilometers of land and water. In the late 1990\'s, <abbr title="Canadian Soil Information Service">CanSIS</abbr> converted the original <abbr title="Canada Land Inventory">CLI</abbr> agriculture datasets to a component-based file structure.</p>\r\n\r\n<ul>\r\n<li><a href="/cansis/nsdb/cli/index.html">Read about <abbr title="Canada Land Inventory">CLI</abbr> agriculture datasets <b>(Alternative Download Information at the Bottom of the Page)</b> </a></li>\r\n</ul>\r\n\r\n<h2>Land Suitability Rating System (LSRS)</h2>\r\n\r\n<ul>\r\n<li><a href="/cansis/publications/manuals/1995-lsrs/index.html">Land Suitability Rating System for Agricultural Crops: 1. Spring-seeded small grains</a></li>\r\n\r\n<li><a href="http://publications.gc.ca/collections/collection_2015/aac-aafc/A59-33-2012-eng.pdf">1961-1990 climate suitability map for spring seeded small grains in the Prairie Provinces (PDF, 3.4 MB)</a></li>\r\n</ul>\r\n\r\n<h2>Soil Order Map of Canada</h2>\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.agr.gc.ca/atlas/soil"><img src="/cansis/interpretations/soil_order_map.gif" alt="" class="image-left blockSpacerRight" /></br>View interactive map</a>\r\n	</div>\r\n</div>\r\n\r\n</br>\r\n<p>The Soil Order map of Canada displays the general distribution of soil types across Canada. The soil order is the highest level (broadest grouping) within the Canadian System of Soil Classification. Soils classified at the order level reflect the climate and landscape characteristics associated with the different regions of Canada.</p>\r\n\r\n<h2>Soil Carbon</h2>\r\n<p>Soil carbon interpretations are found here: <a href="carbon/index.html">Canada\'s Carbon-Sequestering Soil Environments</a></p>\r\n\r\n', '2016-04-26', 'meta', 'Interprétations Agroalimentaires', 'Interprétations Agroalimentaires', '<p>Les produits interprétatives  combinent plusieurs éléments d\'information pour  répondre à des questions spécifiques. Les données de sols et de paysage   peuvent &ecirc;tre combinés avec des données sur le climat, la géologie et l\'utilisation  des terres  pour adresser une  variété d\'issues agro-environnementaux. Quelques exemples comprennent&#160;:</p>\r\n\r\n\r\n<h2>Indicateurs agro-environnementaux</h2>\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.arcgis.com/home/webmap/viewer.html?url=http%3A%2F%2Fwww.agr.gc.ca%2Fatlas%2Frest%2Fservices%2Fmapservices%2Faafc_agri_environmental_indicators%2FMapServer&source=sd"><img src="/cansis/interpretations/indicators_map.gif" alt="" class="image-left blockSpacerRight"/></br>Voir la carte interactive</a>\r\n	</div>\r\n</div>\r\n</br>\r\n\r\n<p>Le Programme national d\'analyse et de rapport en matière de santé  agroenvironnementale (PNARSA) effectue des évaluations et produit des rapports sur le rendement environnemental du secteur agricole à partir  d\'un ensemble d\'indicateurs agroenvironnementaux (IAE). Ces indicateurs visent à fournir des données scientifiques fiables sur l\'état actuel et l\'évolution des conditions de l\'environnement en agriculture.</p>\r\n\r\n\r\n<h2>Inventaire des terres du Canada - Possibilités agricoles des sols</h2>\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.arcgis.com/home/webmap/viewer.html?url=http%3A%2F%2Fwww.agr.gc.ca%2Fatlas%2Frest%2Fservices%2Fservicesdecarte%2Faac_inventaire_des_terres_du_canada%2FMapServer&source=sd"><img src="/cansis/interpretations/cli_map.gif" alt="" class="image-left blockSpacerRight" /></br>Voir la carte interactive</a>\r\n	</div>\r\n</div>\r\n</br>\r\n\r\n<p>L\'Inventaire des terres du Canada (ITC) est un inventaire complet et  pluridisciplinaire des terres rurales du Canada, couvrant plus de  2,5&#160;millions de kilomètres carrés de terres et d\'eau. &Agrave; la fin des  années 1990, le <abbr title="Service d\'information sur les sols du Canada">SISCan</abbr> a converti les ensembles de données originales  sur l\'agriculture de l\'<abbr title="Inventaire des terres du Canada">ITC</abbr> en une structure de fichiers à base de composantes. Ce site permet d\'accéder à ces ensembles de données.</p>\r\n\r\n<ul>\r\n <li><a href="/siscan/nsdb/cli/index.html">Lisez plus</a> </li>\r\n</ul>\r\n\r\n<h2>Classification des terres selon leurs aptitudes pour les cultures</h2>\r\n\r\n<ul>\r\n <li><a href="/siscan/publications/manuals/1995-lsrs/index.html">Système de classification des terres selon leurs aptitudes pour les cultures&#160;:<br />\r\n1. La production des céréales de printemps</a>\r\n\r\n<li><a href="http://publications.gc.ca/collections/collection_2015/aac-aafc/A59-33-2012-eng.pdf">Classification des climats (1961-1990) selon l\'aptitude pour la production des céréales de printemps&#160;: Carte des Prairies (PDF 6.7 Mo)</a></li>\r\n</ul>\r\n\r\n<h2>Carte de l\'ordre des sols du Canada</h2>\r\n\r\n<div class="row">\r\n	<div class="col-md-9">\r\n		<a href="http://www.agr.gc.ca/atlas/pvga?webmap-en=c225cc78d5b142d58eacefae91cc535b&webmap-fr=ad0b6822a33e411683f99979a1167efa"><img src="/cansis/interpretations/soil_order_map.gif" alt="" class="image-left blockSpacerRight" /></br>Voir la carte interactive</a>\r\n	</div>\r\n</div>\r\n</br>\r\n\r\n<p>La carte de l\'Ordre des sols du Canada pr&#233;sente la distribution g&#233;n&#233;rale des types de sols au Canada. L\'Ordre des sols est le niveau de cat&#233;gorie le plus &#233;lev&#233; du Syst&#232;me canadien de classification des sols. Les sols classifi&#233;s &#224; ce niveau refl&#232;tent les climats et les paysages terrestres des diff&#233;rentes r&#233;gions du Canada.</p>\r\n\r\n<h2>Sols piégeurs de carbone du Canada</h2>\r\n<p>Interprétations de carbone du sol se trouvent ici: <a href="carbon/index.html">Sols piégeurs de carbone du Canada</a>.</p>\r\n\r\n', '2016-04-26', 'Migrate to canada.ca'),
	(25, '', 'index', 1, '2019-09-20', 'meta', 'Canadian Soil Information Service', '', '<div class="row">\r\n	<div class="col-md-6">\r\n		<h1 id="wb-cont">Canadian Soil Information Service</h1>\r\n		<p>The Canadian Soil Information Service (CanSIS) is an authoritative source of soil data and land resource information for Canada.</p>\r\n	</div>\r\n	<div class="col-md-6 margin-top-small hidden-sm hidden-xs">\r\n		<img src="images/features/soil-banner.png" alt="Soil Pit" class="pull-right img-responsive thumbnail">\r\n	</div>\r\n</div>\r\n<div class="row">\r\n	<section class="col-md-8">\r\n		<h2>Services and information</h2>\r\n		<div class="row">\r\n			<section class="col-md-6">\r\n				<h3 class="h5"><a href="/cansis/nsdb/index.html">\r\n					National Soil Database\r\n					</a>\r\n				</h3>\r\n				<p>\r\n					The National Soil Database contains soil, landscape, and climatic data for all of Canada\r\n				</p>\r\n			</section>\r\n			<section class="col-md-6">\r\n				<h3 class="h5"><a href="/cansis/interpretations/index.html">\r\n					Soil Interpretations\r\n					</a>\r\n				</h3>\r\n				<p>\r\n					Access land use management and planning tools developed to assist in land assessment and decision making\r\n				</p>\r\n			</section>\r\n			<div class="clearfix"></div>\r\n			<section class="col-md-6">\r\n				<h3 class="h5"><a href="/cansis/publications/surveys/index.html">\r\n					Soil Survey Reports\r\n					</a>\r\n				</h3>\r\n				<p>\r\n					Access individual detailed soil survey reports\r\n				</p>\r\n			</section>\r\n			<section class="col-md-6">\r\n				<h3 class="h5"><a href="/cansis/publications/maps/index.html">\r\n					Printed Maps\r\n					</a>\r\n				</h3>\r\n				<p>\r\n					Access the printed map collection of Canadian soils, landscapes, and climate\r\n				</p>\r\n			</section>\r\n			<div class="clearfix"></div>\r\n			<section class="col-md-6">\r\n				<h3 class="h5"><a href="/cansis/publications/manuals/index.html">\r\n					Canadian Soil Publications Archive\r\n				