	(505, '/publications/manuals/1982-forms', 'index', 1, '2013-06-27', 'meta', 'Manual for Describing Soils in the Field: 1982 Revised', 'Manual for Describing Soils in the Field: 1982 Revised', '<div class="align-center">\r\n<a href="82-52-describing-soils.pdf"><img src="82-52e.jpg" alt="" /><br />Manual for Describing Soils in the Field, 1982 Revised. (PDF Format, 16 MB)</a>\r\n</div>\r\n\r\n<section>\r\n<h2>Citation</h2>\r\n<p class="indent1">Expert Committee on Soil Survey, 1982. The Canada Soil Information System (CanSIS): Manual for Describing Soils in the Field, 1982 Revised. Land Resource Research Institute, Research Branch, Agriculture Canada, Ottawa. <abbr title="Land Resource Research Institute">LRRI</abbr> Contribution <abbr title="number">no</abbr> 82-52. 166 pp.</p>\r\n</section>\r\n\r\n<section>\r\n<h2>Data Input Forms</h2>\r\n<div class="align-center">\r\n<p>\r\n<a href="82-52-green-field-form.pdf"><img src="82-52-green-field-form.jpg" alt="" /><br/>\r\n Field Description Input Form (Green Form) (PDF Format, 7.96 MB)</a>\r\n</p>\r\n<p>\r\n<a href="82-52-gold-lab-form.pdf"><img src="82-52-gold-lab-form.jpg" alt="" /><br/>\r\n Analytical Data Input Form (Gold Form) (PDF Format, 7.60 MB)</a>\r\n</p>\r\n</div>\r\n</section>\r\n\r\n\r\n\r\n', '2013-06-27', 'meta', 'Manuel de description des sols sur le terrain : révision 1982', 'Manuel de description des sols sur le terrain : révision 1982', '<div class="alignCenter">\r\n<p><a href="82-52-description-des-sols.pdf"><img src="82-52f.jpg" alt="" /><br />Manuel de description des sols sur le terrain: révision 1982 (Format PDF, 14 Mo)</a>\r\n</div>\r\n\r\n<section>\r\n<h2>Référence bibliographique : </h2>\r\n<p class="indent1">Comit&eacute; d\'experts sur la prospection p&eacute;dologique, 1982. Syst&egrave;me d\'informatique des sols au Canada : Manuel de description des sols sur le terrain, r&eacute;vision 1982. Institut de recherches sur les terres, Direction g&eacute;n&eacute;rale de la recherche, Agriculture Canada, Ottawa. <abbr title="Institut de recherches sur les terres">I.R.T.</abbr> Contribution <abbr title="numéro">no</abbr> 82-52. 196 <abbr title="pages">p.</abbr></p>\r\n</section>\r\n\r\n<section>\r\n<h2>Formulaires de saisie de données</h2>\r\n<div class="align-center">\r\n<p>\r\n<a href="82-52-donnee-de-champ.pdf"><img src="82-52-donnee-de-champ.jpg" alt="" /><br/>\r\nDocument d\'entrée de la description au champ (Format PDF, 1.6 Mo)</a>\r\n</p>\r\n<p>\r\n<a href="82-52-donnee-analytiques.pdf"><img src="82-52-donnee-analytiques.jpg" alt="" /><br/>\r\nMéthodes et données analytiques (Format PDF, 1.4 Mo)</a>\r\n</p>\r\n</div>\r\n</section>\r\n\r\n\r\n', '2013-06-27', 'Migrate to publications.gc.ca'),
	(506, '/nsdb/dss', 'index', 1, '2013-04-18', 'meta', 'Overview of Detailed Soil Surveys', 'Detailed Soil Surveys', '<p>Soil surveys have been published for most of the agricultural areas, and many surrounding areas, across Canada. Data from these surveys comprise the most detailed soil inventory information in the <a title="National Soil DataBase" href="/cansis/nsdb/index.html">NSDB</a>.</p>\r\n\r\n<ul>\r\n<li>Version 1 was the electronic data structure (SMUF/SNF/SLF) used in the original CanSIS system dating from the early 1970\'s.</li>\r\n\r\n<li><a href="v2/index.html">Version 2</a> introduced a new data structure based on components. This version was created in the 1990\'s by converting version 1 data.</li>\r\n\r\n<li><a href="v3/index.html">Version 3</a> was created in the 2010\'s by amalgamating version 2 data. It introduced some minor refinements to the version 2 data structure to provide closer alignment with the <a title="Soil Landscapes of Canada" href="/cansis/nsdb/slc/index.html">SLC</a> data structure.</li>\r\n\r\n</ul>\r\n', '2013-04-18', 'meta', 'Aperçu des levés pédologiques détaillés ', 'Levés pédologiques détaillés', '<p>Des levés pédologiques ont été publiés pour la plupart des régions agricoles et de nombreuses régions avoisinantes à l\'échelle du Canada. Les données tirées de ces levés renferment l\'information la plus détaillée sur l\'inventaire des sols dans la <a title="Base nationale de données sur les sols" href="/siscan/nsdb/index.html">BNDS</a>.</p>\r\n\r\n<ul>\r\n<li><a href="v2/index.html">Version 2</a></li>\r\n\r\n<li><a href="v3/index.html">Version 3</a></li>\r\n</ul>\r\n', '2013-04-18', 'Retire');
